    Va=msg->getRi();
    //bgw
    siz = msg->getSize();
    //bgw
    for (k=0; k<len1; k++)
        Res0[k]=msg->getRes(k);
    i=0;
    len11=msg->getSize();
    for (k=5; k<(siz-1); k++){
        Res01[i]=msg->getRes1(k);
        i++;
    }
    //////////////////////////////////dchiffrement de res01=(v^p)////////////////////////////////////////////

    for (i=0; i<len11; i++)
        tabx[i]=key0N[i]-'0';

    for (k=0; k<len11; k++)
    {
        if (Res01[k]==-1)
            concat[k]=-1;
        else
            concat[k]=Res01[k] ^ tabx[k+5];
    }
    /////////////////////////////////////convert V^p to decimal////////////////////////////////////////////////////
    n=len11/4;
