    int decimal = 0 ;
    k,i,j,y=0;

    for(j = 0 ; j < len11 ; j++)
    {
        for (i=0; i<4; i++)
        {
            decimal=(decimal*2)+concat[j];
            j++;
        }
        j=j-1;
        i=0;
        tableau2[y]=decimal+'0';
        decimal=0;
        y++;
    }
    rnd=BN_new();
    BN_dec2bn(&rnd, tableau2);
    char* hbltn3;
