    //////////////////////////////////Now the node have to compute V^p^q/////////////////////////////////////////////////////////////

    BN_CTX *Ctx = BN_CTX_new();
    BN_exp(resl,rnd,x,Ctx);
    Xm =BN_bn2dec(resl);
    len1=strlen(Xm);
    //////////////////////////////convert Xm(V^p^q) to binary//////////////////////////////////////////////////////

    string myString = reinterpret_cast<const char*>(Xm);
