    len111=Vpq.size();
    //int VpqBin[len111];
    for (i=0; i<len111; i++)
        tableau[i]=Vpq[i]-'0';
    /////////////////////////////////////concat ID and V^p^q/////////////////////////////////////////////////////////////
    siz=4+200+2;
    /////////////////////////////////concat the results ////////////////////////////////////////////
    int concat2[siz];
    for (i=0; i<siz; i++) concat2[i]=-2;
    ///////////////////////////////////////////////////////////////////////////////////////////////
    for (k=0; k<4; k++) concat2[k]=IDbin[k];
    concat2[k]= -1;
    for (k=5; k<(200+5); k++)
    {
        concat2[k]=tableau[Va];
        Va++;
    }
    concat2[k]= -1;
    ///////////////////////////////cipher////////////////////////////////////////////////
    //Res01 est un tableau qui contient le resultat du "ou exclusif" entre concat2 et key0N
    for (i=0; i<siz; i++)
    {
        if (concat2[i]==-1)
            Res01[i]=-1;
        else
        {
            if (concat2[i]==-2)
                Res01[i]=-2;
            else
                Res01[i]= tabx[i] ^ concat2[i];
        }
    }
    ////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    for (k=0; k<len; k++)
        msg->setFkey(k, Xm[k]);


